mapper.update:
update tbl_cart set
p_count = #{p_count}
where u_id = #{u_id} and c_num = #{c_num}

update tbl_product_count set
p_count = p_count - #{p_count}
where p_num = #{p_num}
and p_size = #{p_size}

mapper.select:
select count(*) from tbl_cart
where u_id = #{u_id}
and p_num = #{p_num}
and p_status = 1001
and p_size = #{p_size}

mapper.update:
update tbl_cart set
p_count = p_count + #{p_count}
where u_id = #{u_id} and p_size = #{p_size} and p_num = #{p_num}

update tbl_cart set
p_status = 1006
where c_num = #{c_num}

mapper.select:
select count(*) from tbl_cart
where u_id = #{u_id}
and p_status != 1001

select p_count from tbl_product_count
where p_num = #{p_num}
and p_size = #{p_size}
